from flask import Flask, render_templateapp = Flask(__name__)@app.route('/')def home():    return render_template('home.html')@app.route('/detail')def detail():    return render_template('detail.html')@app.route('/detailsiswa')def detailsiswa():    return render_template('detailsiswa.html')if __name__ == '__main__':    app.run(debug=True)